import React, { Component } from 'react';
import {Card, Button, Form, Alert, Dropdown, DropdownButton} from 'react-bootstrap';
import '../css/login.css';

export default class NewAccount extends Component {

    state = {
        userType: "Unknown"
    } 

    // const [FirstName, setFirstName] = useState("");
	// const [LastName, setLastName] = useState("");
	// const [Address, setAddress] = useState("");
	// const [City, setCity] = useState("");
	// const [USState, setUSState] = useState("");
	// const [ZipCode, setZipCode] = useState("");
	// const [PhoneNumber, setPhoneNumber] = useState("");

    handleIsPilot = () =>{
        const userType = "Pilot";
        this.setState({userType});
    }

    handleIsPassenger = () =>{
        const userType = "Passenger";
        this.setState({userType});
    }

    updateDropdown(){
        if(this.state.userType == "Pilot"){
            console.log("Pilot");
            return "I am a Pilot";
        }else if(this.state.userType == "Passenger"){
            console.log("Passenger");
            return "I am a Passenger";
        }else{
            
            return "Are you registering as a Passenger or as a Pilot?"
        }
    }  

    render() { 
        this.inst();
        return (
            <div className="p-4 centeronscreen centertext loginwidth">
                <h2 className="mb-3">Create a New Account</h2>
                <Form>
                    <Form.Group className="mb-3 flex" controlId="name">
                        <Form.Control
                            className="formMedium"
                            type='FirstName' 
                            placeholder='First Name'
                            //onChange={ (e) => setFirstName(e.target.value)}
                        />
                        <Form.Control
                            className="formMedium"
                            type='LastName' 
                            placeholder='Last Name'
                            //onChange={ (e) => setLastName(e.target.value)}					
                        />
                     </Form.Group>
                    <Form.Group className="mb-3 flex" controlId="address1">
                        <Form.Control
                            className="formMedium"
                            type='Address' 
                            placeholder='Address'
                              //onChange={ (e) => setAddress(e.target.value)}					
                        />
                        <Form.Control
                            className="formMedium"
                            type='City' 
                            placeholder='City'
                            //onChange={ (e) => setCity(e.target.value)}						
                        />
                    </Form.Group>
                    <Form.Group className="mb-3 flex" controlId="USState">
                        <Form.Control
                            className="formSmall"
                            type='USState' 
                            placeholder='State'
                            //onChange={ (e) => setUSState(e.target.value)}					
                        />
                        <Form.Control
                            className="formSmall"
                            type='ZipCode' 
                            placeholder='Zip'
                            //onChange={ (e) => setZipCode(e.target.value)}					
                        />
                        <Form.Control
                            className="formMedium"
                            type='PhoneNumber' 
                            placeholder='Phone #'
                            //onChange={ (e) => setPhoneNumber(e.target.value)}					
                        />
                    </Form.Group>
                    <DropdownButton 
                        className="mb-3 formMedium"
                        controlID="dropdown-basic-button" 
                        title={this.updateDropdown()}>
                        <Dropdown.Item
                            onClick={this.handleIsPilot}
                            >I am a Pilot
                        </Dropdown.Item>
                        <Dropdown.Item
                            onClick={this.handleIsPassenger}
                            >I am a Passenger
                        </Dropdown.Item>
                    </DropdownButton>
                </Form>
            </div>
        );
    }
}
